mapper.select:
SELECT EXISTS
(
SELECT * FROM performance
WHERE title = #{title} AND showType = #{showType}
)

mapper.update:
UPDATE performance
SET imageFilePath = #{filePath}
WHERE id = #{performanceId}

mapper.select:
SELECT imageFilePath
FROM performance
WHERE id = #{performanceId}

SELECT EXISTS
(
SELECT * FROM performance
WHERE id = #{performanceId}
)

SELECT EXISTS
(
SELECT * FROM performance
WHERE id != #{performanceId} AND title = #{title} AND showType = #{showType}
)

mapper.update:
UPDATE performance
SET title = #{perfUpdateRequest.title}, detail = #{perfUpdateRequest.detail},
ageLimit = #{perfUpdateRequest.ageLimit}, showType = #{perfUpdateRequest.showType}
WHERE id = #{performanceId}

mapper.select:
SELECT EXISTS
(
SELECT * FROM performance

    WHERE id = #{lastPerfId} AND showType = #{showType}


    WHERE id = #{lastPerfId}


    WHERE showType = #{showType}

)

SELECT p.id AS id, p.title AS title, p.imageFilePath AS imageFilePath, v.name AS venueName, vh.name AS hallName FROM performance p INNER JOIN venue v ON p.venueId = v.id INNER JOIN venueHall vh ON p.hallId = vh.id
WHERE p.id < #{pagination.lastPerfId} AND showType = #{showType}
WHERE p.id < #{pagination.lastPerfId}
WHERE showType = #{showType}
ORDER BY p.id DESC LIMIT #{pagination.amount}
