mapper.select:
SELECT EXISTS (SELECT * FROM venueHall WHERE name IN

    #{venueHallUpdate.name}

AND venueId = #{venueId}
)

update.foreach:
UPDATE venueHall

    name=#{venueHallUpdate.name},
    rowSeats=#{venueHallUpdate.rowSeats},
    columnSeats=#{venueHallUpdate.columnSeats},
    seatingCapacity=#{venueHallUpdate.seatingCapacity}

WHERE venueId = #{venueId} AND id = #{updateHallIds[${index}]}

mapper.delete:
DELETE FROM venueHall WHERE id IN

        #{deleteHallId}

AND venueId = #{venueId}
