mapper.update:
UPDATE venue
SET name = #{updateRequest.name}, address = #{updateRequest.address}, tel = #{updateRequest.tel}, homepage = #{updateRequest.homepage}
WHERE id = #{venueId}

mapper.select:
SELECT id, name, address, tel, homepage
FROM venue
LIMIT #{startPage}, #{amount}

SELECT id, name, address, tel, homepage
FROM venue
WHERE id = #{venueId}
